INSERT INTO lezione (id, materia, sopratitolo, titolo, descrizione, testo)
VALUES 
('ita2', 'Italiano', 'Lezione', 'Giosuè Carducci', 'Approfondisci la vita, le opere e l''influenza di Giosuè Carducci, illustre poeta italiano insignito del Premio Nobel per la Letteratura nel 1906. Esplora il contesto storico, sociale e culturale in cui Carducci ha operato, analizzando le tematiche ricorrenti e lo stile poetico caratteristico delle sue opere. Dai un''occhiata più da vicino alla sua produzione, che spazia dalla poesia lirica al canto patriottico, e scopri come il suo contributo abbia plasmato la letteratura italiana dell''Ottocento e oltre.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\lezione 2-Giosuè Carducci.md')),
('ita3', 'Italiano', 'Lezione', 'Realismo, Naturalismo, Verismo', 'Immergiti nell''ambiente letterario italiano del XIX secolo e esplora i movimenti del Realismo, del Naturalismo e del Verismo. Approfondisci le caratteristiche distintive di ciascun movimento, analizzando le opere e gli autori più rappresentativi. Scopri come questi movimenti abbiano influenzato la narrativa, il teatro e la critica letteraria, delineando nuove prospettive sulla rappresentazione della realtà e sul ruolo dell''artista nella società dell''epoca.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\lezione 3 - Realismo, Naturalismo, Verismo.md')),
('ita5', 'Italiano', 'Lezione', 'Giovanni Verga', 'Esamina a fondo la vita e le opere di Giovanni Verga, uno dei più eminenti scrittori italiani del XIX secolo. Analizza il realismo crudo e la profonda introspezione psicologica presenti nelle sue opere, con particolare attenzione a romanzi come "I Malavoglia" e "La roba". Scopri come Verga abbia ritratto con maestria la vita dei contadini siciliani e la società dell''epoca, lasciando un''impronta indelebile nella letteratura italiana.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\lezione 5-Giovanni Verga.md')),
('ita6', 'Italiano', 'Lezione', 'II ciclo dei vinti', 'Approfondisci lo studio delle opere appartenenti al ciclo dei vinti nella letteratura italiana, con particolare focus sulle opere di Giovanni Verga. Esplora i temi centrali, i personaggi e lo stile narrativo caratteristico di questo ciclo, che offre una rappresentazione realistica e spesso tragica della condizione umana e sociale nell''Italia dell''Ottocento. Analizza in dettaglio romanzi come "I Malavoglia" e "Mastro don Gesualdo", evidenziando le influenze del realismo e del naturalismo.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\lezione 6-II ciclo dei vinti.md')),
('ita7', 'Italiano', 'Lezione', 'I Malavoglia', 'Concentrati sull''analisi approfondita de I Malavoglia, uno dei capolavori di Giovanni Verga e un''icona della letteratura realista italiana. Esplora i temi della lotta per la sopravvivenza, dell''onore familiare e delle tensioni sociali presenti nel romanzo, e scopri come Verga abbia dipinto con vividezza e realismo la vita dei pescatori siciliani. Approfondisci la struttura narrativa, i personaggi e il contesto storico-culturale in cui è ambientata l''opera, evidenziando il suo significato e la sua rilevanza nella letteratura italiana dell''Ottocento.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\lezione 7-I Malavoglia.md')),
('ita8', 'Italiano', 'Lezione', 'La roba', 'Analisi approfondita de La roba, un romanzo di Giovanni Verga che si distingue per la sua rappresentazione realistica e implacabile del mondo contadino siciliano. Esplora i temi dell''avidità, della lotta per il potere e delle disuguaglianze sociali presenti nell''opera, e analizza come Verga abbia utilizzato lo stile narrativo e la lingua per trasmettere con efficacia le sue idee. Approfondisci la struttura del romanzo, i personaggi chiave e il loro sviluppo, evidenziando il contributo di Verga alla letteratura italiana del XIX secolo.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 8 - La roba.md')),
('ita9', 'Italiano', 'Lezione', 'II Decadentismo', 'Esplora il movimento letterario del Decadentismo, un periodo di ribellione e trasgressione nella cultura europea del tardo XIX secolo. Analizza le caratteristiche distintive di questo movimento, tra cui la ricerca dell''estetica del Bello, la fascinazione per il decadimento e la malattia, e l''esplorazione dei tabù sessuali e sociali. Studia le opere e gli autori rappresentativi di questa corrente letteraria, esaminando il loro impatto sulla letteratura e sulla società dell''epoca.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 9 - II Decadentismo.md')),
('ita10', 'Italiano', 'Lezione', 'II Simbolismo e Baudelaire', 'Approfondisci lo studio del Simbolismo in letteratura e dell''opera di Charles Baudelaire, poeta e critico d''arte francese considerato uno dei precursori del movimento. Esplora i temi centrali del Simbolismo, tra cui il mistero, il sogno e l''inconscio, e analizza come questi siano stati espressi nelle opere di Baudelaire, in particolare ne "I fiori del male". Scopri come il Simbolismo abbia influenzato la poesia e l''arte europea della fine del XIX secolo, aprendo nuove prospettive estetiche e espressive.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 10-II Simbolismo e Baudelaire.md')),
('ita11', 'Italiano', 'Lezione', 'Oscar Wilde ''Il ritratto di Dorian Gray''', 'Analisi dettagliata del romanzo ''Il ritratto di Dorian Gray'' di Oscar Wilde, opera considerata un classico della letteratura inglese e uno dei capolavori del Decadentismo. Esplora i temi della bellezza, della moralità e del peccato presenti nel romanzo, e analizza come Wilde abbia utilizzato il genere del romanzo gotico per esplorare le contraddizioni della natura umana. Approfondisci la struttura narrativa, i personaggi principali e il loro sviluppo, evidenziando la ricchezza e la complessità dell''opera.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 11-Oscar Wilde ''Il ritratto di Dorian Gray''.md')),
('ita13', 'Italiano', 'Lezione', 'Gabriele D''Annunzio', 'Approfondisci l''analisi delle opere e dello stile di Gabriele D''Annunzio, uno dei più importanti e controversi scrittori italiani della fine del XIX e dell''inizio del XX secolo. Esplora la ricchezza linguistica e la sensualità delle sue opere, analizzando i temi dell''amore, della bellezza e del desiderio presenti nei suoi romanzi e nelle sue poesie. Studia il contesto storico e culturale in cui D''Annunzio ha operato, e scopri come abbia influenzato la letteratura e l''arte del suo tempo.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 13-Gabriele D''Annunzio.md')),
('ita14', 'Italiano', 'Lezione', 'II Piacere', 'Analisi approfondita del romanzo "Il Piacere" di Gabriele D''Annunzio, considerato uno dei capolavori della letteratura italiana del XX secolo. Esplora i temi della passione, dell''arte e del desiderio presenti nell''opera, e analizza come D''Annunzio abbia utilizzato la narrazione per esplorare le sfumature della psiche umana. Approfondisci la struttura del romanzo, i personaggi chiave e il loro sviluppo, evidenziando il suo significato e la sua rilevanza nella letteratura italiana.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 14-II Piacere.md')),
('ita16', 'Italiano', 'Lezione', 'Le Laudi', 'Analisi dettagliata del poemetto "Le Laudi" di Giosuè Carducci, una delle opere più celebri del poeta italiano. Esplora il contesto storico e culturale in cui è stata scritta l''opera, e analizza i temi della fede, della patria e della natura presenti nel poemetto. Approfondisci lo stile poetico di Carducci, con particolare attenzione alle immagini e alle metafore utilizzate per descrivere la bellezza e la grandezza della vita e della natura.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\lezione 16-Le Laudi.md')),
('ita17', 'Italiano', 'Lezione', 'Giovanni Pascoli', 'Esplora la vita e le opere di Giovanni Pascoli, uno dei principali poeti italiani della fine del XIX e dell''inizio del XX secolo. Analizza il linguaggio poetico e le tematiche ricorrenti nelle sue opere, tra cui la natura, l''infanzia e la memoria. Approfondisci il legame di Pascoli con il territorio e la sua ricerca di un linguaggio poetico semplice e autentico, che lo ha reso uno degli autori più amati e studiati della letteratura italiana moderna.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\lezione 17-Giovanni Pascoli.md')),
('ita20', 'Italiano', 'Lezione', 'Le Avanguardie', 'Scopri il mondo delle Avanguardie nella letteratura italiana del XX secolo. Esplora i movimenti artistici e letterari che hanno rivoluzionato la percezione dell''arte e della cultura, dal Futurismo al Surrealismo. Analizza le tecniche e le tematiche caratteristiche delle Avanguardie, e studia le opere e gli artisti più rappresentativi di questo periodo di fermento e innovazione.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 20-Le Avanguardie.md')),
('ita21', 'Italiano', 'Lezione', 'La Grande Guerra nella letteratura', 'Esplora il tema della Grande Guerra nella letteratura italiana del XX secolo. Analizza come gli scrittori hanno rappresentato l''esperienza della guerra, le sue conseguenze e il suo impatto sulla società e sulla cultura. Studia le opere di autori come Ungaretti, Saba e Marinetti, e scopri come abbiano utilizzato la narrazione per esprimere le loro visioni e le loro emozioni di fronte alla tragedia della guerra.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 21-La Grande Guerra nella letteratura.md')),
('ita22', 'Italiano', 'Lezione', 'Eugenio Montale', 'Approfondisci lo studio della vita e delle opere di Eugenio Montale, uno dei più importanti poeti italiani del XX secolo. Esplora il linguaggio poetico di Montale, caratterizzato da una ricerca di chiarezza ed essenzialità, e analizza le tematiche ricorrenti nelle sue poesie, tra cui l''amore, il tempo e il rapporto con la natura. Studia il contesto storico e culturale in cui Montale ha operato, e scopri come abbia influenzato la poesia italiana del Novecento.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 22-Eugenio Montale.md')),
('ita23', 'Italiano', 'Lezione', 'I Malavoglia', 'Approfondimento della struttura, dei personaggi e dei temi principali del romanzo "I Malavoglia" di Giovanni Verga, capolavoro del realismo italiano. Esplora la vita dei pescatori siciliani e le loro lotte quotidiane per la sopravvivenza, analizzando la struttura narrativa del romanzo e il suo significato storico e sociale. Studia i personaggi principali, e scopri come Verga abbia dipinto con realismo e intensità la vita della famiglia Malavoglia.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 23-I Malavoglia.md')),
('ita24', 'Italiano', 'Lezione', 'Le Operette Morali', 'Analisi delle "Operette Morali" di Giacomo Leopardi, una raccolta di dialoghi, racconti e aforismi che rappresentano uno dei vertici della prosa leopardiana. Esplora i temi dell''esistenza umana, della conoscenza e del destino presenti nell''opera, e analizza come Leopardi abbia utilizzato la forma dell''operetta per esprimere le sue riflessioni filosofiche e letterarie. Approfondisci la struttura del testo, i personaggi e le tematiche trattate, evidenziando il loro significato nella produzione leopardiana.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 24-Le Operette Morali.md')),
('ita25', 'Italiano', 'Lezione', 'Il passaggio dal romanticismo al realismo', 'Esplora il passaggio dal Romanticismo al Realismo nella letteratura italiana del XIX secolo. Analizza le cause e le conseguenze di questo cambiamento di paradigma letterario, studiando le opere e gli autori più rappresentativi di entrambi i movimenti. Scopri come il Romanticismo abbia enfatizzato l''individuo e la sua interiorità, mentre il Realismo si sia concentrato sulla rappresentazione obiettiva della realtà e delle condizioni sociali.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 25-Il passaggio dal romanticismo al realismo.md')),
('ita26', 'Italiano', 'Lezione', 'L''esperienza del ''900', 'Analisi della letteratura italiana del Novecento, un periodo contrassegnato da profondi mutamenti culturali, politici e sociali. Esplora le diverse correnti e tendenze letterarie del XX secolo, tra cui il Futurismo, il Neorealismo e l''Esistenzialismo, e studia le opere e gli autori più rappresentativi di ciascuna corrente. Scopri come la letteratura italiana abbia risposto alle sfide del Novecento, riflettendo sulle trasformazioni della società e sulle crisi dell''individuo.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 26-L''esperienza del ''900.md')),
('ita27', 'Italiano', 'Lezione', 'Luigi Pirandello', 'Esplora la vita e le opere di Luigi Pirandello, uno dei più importanti drammaturghi e narratori italiani del XX secolo. Analizza il suo stile narrativo innovativo e la sua esplorazione della natura umana, attraverso opere come "Sei personaggi in cerca d''autore" e "Uno, nessuno e centomila". Studia il suo rapporto con il teatro e la sua concezione dell''arte come rappresentazione della molteplicità e dell''instabilità dell''essere umano.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 27-Luigi Pirandello.md')),
('ita29', 'Italiano', 'Lezione', 'Il Piacere', 'Analisi approfondita del romanzo "Il Piacere" di Gabriele D''Annunzio, considerato uno dei capolavori della letteratura italiana del XX secolo. Esplora i temi della passione, dell''arte e del desiderio presenti nell''opera, e analizza come D''Annunzio abbia utilizzato la narrazione per esplorare le sfumature della psiche umana. Approfondisci la struttura del romanzo, i personaggi chiave e il loro sviluppo, evidenziando il suo significato e la sua rilevanza nella letteratura italiana.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 29 - Il Piacere.md')),
('ita30', 'Italiano', 'Lezione', 'Dino Buzzati', 'Esplora la vita e le opere di Dino Buzzati, uno dei più importanti scrittori italiani del XX secolo. Analizza i temi ricorrenti nei suoi racconti e romanzi, tra cui la dimensione fantastica, l''angoscia esistenziale e il rapporto tra sogno e realtà. Studia il suo stile narrativo, caratterizzato da una scrittura precisa e suggestiva, e scopri come abbia influenzato la letteratura e la cultura italiane del secondo dopoguerra.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 30 - Dino Buzzati.md')),
('ita31', 'Italiano', 'Lezione', 'Italo Calvino', 'Approfondisci lo studio della vita e delle opere di Italo Calvino, uno dei più importanti scrittori italiani del Novecento. Esplora la sua produzione letteraria, che spazia dal realismo magico alle narrazioni postmoderne, e analizza i temi ricorrenti nei suoi romanzi e racconti, tra cui l''immaginazione, il gioco linguistico e il rapporto tra fantasia e realtà. Studia il suo stile narrativo e la sua capacità di reinventarsi continuamente, e scopri come abbia influenzato la letteratura italiana e mondiale.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 31 - Italo Calvino.md')),
('ita32', 'Italiano', 'Lezione', 'Alberto Moravia', 'Esplora la vita e le opere di Alberto Moravia, uno dei principali scrittori italiani del XX secolo. Analizza il suo stile narrativo e la sua capacità di indagare la psicologia umana attraverso opere come "Gli indifferenti" e "La noia". Studia i temi ricorrenti nei suoi romanzi, tra cui l''alienazione, la sessualità e la disillusione, e scopri come Moravia abbia influenzato la letteratura italiana e la cultura del suo tempo.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 32 - Alberto Moravia.md')),
('ita33', 'Italiano', 'Lezione', 'Il Neorealismo', 'Esplora il movimento cinematografico e letterario del Neorealismo italiano. Analizza le caratteristiche distintive di questo movimento, che ha posto l''accento sulla rappresentazione realistica della vita quotidiana e delle condizioni sociali dell''Italia del dopoguerra. Studia le opere e gli autori più rappresentativi del Neorealismo, e scopri come abbiano influenzato il cinema e la letteratura mondiale.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 33 - Il Neorealismo.md')),
('ita34', 'Italiano', 'Lezione', 'Primo Levi', 'Esplora la vita e le opere di Primo Levi, uno dei più importanti scrittori e testimoni dell''Olocausto. Analizza la sua opera più celebre, "Se questo è un uomo", e scopri come Levi abbia raccontato la sua esperienza nei campi di concentramento nazisti con onestà e lucidità. Studia il suo stile narrativo e la sua capacità di dare voce alle vittime del genocidio, e scopri come abbia influenzato la letteratura e la cultura italiane e mondiali.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 34 - Primo Levi.md')),
('ita35', 'Italiano', 'Lezione', 'Elsa Morante', 'Approfondisci lo studio della vita e delle opere di Elsa Morante, una delle più importanti scrittrici italiane del XX secolo. Esplora i temi ricorrenti nei suoi romanzi, tra cui la memoria, l''infanzia e il rapporto tra individuo e storia. Analizza opere come "La storia" e "L''isola di Arturo", e scopri come Morante abbia utilizzato la narrazione per esplorare le complessità dell''esperienza umana e sociale.', LOAD_FILE('C:\\Users\\mazza\\OneDrive\\Documenti\\Obsidian Vault\\Italiano e Storia\\Lezione 35 - Elsa Morante.md'));
